<style lang="scss">
@keyframes shadow {
  from {
    box-shadow: 0 0 0 1px rgba(239, 234, 230, 0.1);
  }
  to {
    box-shadow: 0 0 0 20px rgba(239, 234, 230, 0);
    background-color: rgba(239, 234, 230, 0);
  }
}
.social-icons {
  display: flex;
  & > div {
    width: 40px;
    height: 40px;
    cursor: pointer;
    z-index: 1;
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    & > svg {
      width: 20px;
      height: 20px;
    }
    &:before {
      content: "";
      display: block;
      width: 40px;
      height: 40px;
      border-radius: 100%;
      background-color: rgba(239, 234, 230, 0.1);
      position: absolute;
      top: 0px;
      left: 0px;
      right: 0px;
      bottom: 0px;
    }
    &:hover {
      & > svg > g > path {
        fill: #fff;
      }
      &:before {
        animation: shadow 300ms forwards;
      }
    }
  }
}
</style>
