<script>
import axios from "axios";
import anime from "animejs";
import errorProcessing from "../utils/errorProcessing";
import { MODEL, REQUEST } from "../queries";

export default {
  name: "home",
  metaInfo: {
    title: process.env.VUE_APP_TITLE + " | Обучение шахматам по всей России"
  },
  data: () => ({
    trainer_header: "Наши тренеры",
    trainers: [],
    contact: ""
  }),
  created() {
    axios
      .post(MODEL, {
        query: `
        query {
          topTrainers {
            user {
              id
              fullName
            }
            image {
              image
            }
          }
        }
      `
      })
      .then(res => {
        this.trainers = res.data.data.topTrainers;
      })
      .catch(err => {
        if (err.response === undefined) {
          this.$store.commit("connectionRefused", {
            refused: this.created
          });
        }
        errorProcessing(err);
      });
  },
  mounted() {
    document.querySelector(".pre-footer").style.height = 0;
  },
  beforeDestroy() {
    document.querySelector(".pre-footer").style.height = "20px";
  },
  methods: {
    path(image) {
      return process.env.VUE_APP_HOST + image;
    },
    chooseTrainer(event, user_id) {
      let { x, y } = event.target.children[0].getBoundingClientRect();
      let img = document.createElement("img");
      let imageStyle = {
        position: "absolute",
        zIndex: "3000",
        top: y + window.scrollY + "px",
        left: x + "px",
        width: event.target.children[0].width + "px",
        height: event.target.children[0].height + "px"
      };
      for (let el in imageStyle) {
        img.style[el] = imageStyle[el];
      }
      img.src = event.target.children[0].src;
      img.height = event.target.children[0].clientWidth;

      let { x: x1, y: y1 } = this.$refs.trainer_header.getBoundingClientRect();
      let clone = this.$refs.trainer_header.cloneNode(false);
      let headerStyle = {
        position: "absolute",
        top: y1 + window.scrollY + "px",
        left: x1 + "px",
        zIndex: "3001"
      };
      for (let el in headerStyle) {
        clone.style[el] = headerStyle[el];
      }
      clone.innerHTML = this.$refs.trainer_header.innerHTML;
      document.body.appendChild(img);
      document.body.appendChild(clone);
      this.$store.commit("trainerDetermine", {
        avatar: img,
        name: clone
      });
      this.$router.push({ name: "Тренер", query: { user_id } });
    },
    chooseRate(event) {
      event.preventDefault();
      event.target.classList.add("active");
      setTimeout(() => {
        event.target.parentNode.nextSibling.classList.remove("d-none");
        anime({
          targets: event.target.parentNode.nextSibling,
          opacity: 1,
          duration: 1000
        });
      }, 500);
    },
    request(event, rate_type) {
      anime({
        targets: event.target,
        opacity: 0,
        duration: 500
      });
      setTimeout(() => {
        event.target.style.display = "none";
        event.target.nextSibling.classList.remove("d-none");
        anime({
          targets: event.target.nextSibling,
          opacity: 1,
          duration: 500
        });
      }, 500);
      axios.post(REQUEST, { rate_type, contact: this.contact }).catch(err => {
        if (err.response === undefined) {
          this.$store.commit("connectionRefused", {
            refused: this.request
          });
        }
        errorProcessing(err);
      });
    }
  }
};
</script>

